mapper.select:
SELECT id, user_id, credential_type, secret_hash, salt, status, expires_at, created_at
FROM auth_credentials

    AND id = #{id}
    AND user_id = #{userId}
    AND credential_type = #{credentialType}
    AND secret_hash = #{secretHash}
    AND salt = #{salt}
    AND status = #{status}
    AND expires_at = #{expiresAt}

ORDER BY id DESC

mapper.update:
UPDATE auth_credentials

    user_id = #{record.userId},
    credential_type = #{record.credentialType},
    secret_hash = #{record.secretHash},
    salt = #{record.salt},
    status = #{record.status},
    expires_at = #{record.expiresAt},
    version = #{record.version},

WHERE id = #{id}
  AND user_id = #{userId}
  AND version = #{expectedVersion}
  AND status = #{currentStatus}
