mapper.select:
SELECT
    *
FROM orders

    AND order_id = #{orderId}
    AND user_id = #{userId}
    AND instrument_id = #{instrumentId}
    AND client_order_id = #{clientOrderId}
    AND side = #{side}
    AND intent = #{intent}
    AND close_cost_price = #{closeCostPrice}
    AND type = #{type}
    AND status = #{status}

mapper.update:
UPDATE orders
status = #{record.status}, price = #{record.price}, quantity = #{record.quantity}, filled_quantity = #{record.filledQuantity}, remaining_quantity = #{record.remainingQuantity}, avg_fill_price = #{record.avgFillPrice}, fee = #{record.fee}, intent = #{record.intent}, close_cost_price = #{record.closeCostPrice}, version = #{record.version}, submitted_at = #{record.submittedAt}, filled_at = #{record.filledAt},
WHERE order_id = #{orderId} AND user_id = #{userId} AND version = #{expectedVersion}

UPDATE orders
status = #{record.status}, price = #{record.price}, quantity = #{record.quantity}, filled_quantity = #{record.filledQuantity}, remaining_quantity = #{record.remainingQuantity}, avg_fill_price = #{record.avgFillPrice}, fee = #{record.fee}, intent = #{record.intent}, close_cost_price = #{record.closeCostPrice}, version = #{record.version}, submitted_at = #{record.submittedAt}, filled_at = #{record.filledAt},
WHERE order_id = #{orderId} AND user_id = #{userId} AND status = #{currentStatus}
