mapper.select:
SELECT
    *
FROM orders

    AND order_id = #{orderId}
    AND user_id = #{userId}
    AND instrument_id = #{instrumentId}
    AND client_order_id = #{clientOrderId}
    AND side = #{side}
    AND intent = #{intent}
    AND close_cost_price = #{closeCostPrice}
    AND type = #{type}
    AND status = #{status}

mapper.update:
UPDATE orders
SET status = #{targetStatus},
    version = version + 1
    , submitted_at = #{submittedAt}
    , filled_at = #{filledAt}
WHERE order_id = #{orderId}
  AND user_id = #{userId}
  AND version = #{expectedVersion}

UPDATE orders

    status = #{targetStatus},
    version = version + 1
    , submitted_at = #{submittedAt}
    , filled_at = #{filledAt}

WHERE order_id = #{orderId}
  AND user_id = #{userId}
  AND status = #{currentStatus}

UPDATE orders

    status = #{targetStatus},
    version = version + 1
    , submitted_at = #{submittedAt}
    , filled_at = #{filledAt}
    , close_cost_price = #{closeCostPrice}

WHERE order_id = #{orderId}
  AND user_id = #{userId}
  AND status = #{currentStatus}
