mapper.update:
UPDATE auth_credentials_pending
SET status = #{status},
    retry_count = 0,
    last_error = NULL,
    next_retry_at = NULL,
    updated_at = #{updatedAt}
WHERE user_id = #{userId}
  AND credential_type = #{credentialType}

UPDATE auth_credentials_pending
SET status = #{status},
    retry_count = retry_count + 1,
    last_error = #{lastError},
    next_retry_at = #{nextRetryAt},
    updated_at = #{updatedAt}
WHERE user_id = #{userId}
  AND credential_type = #{credentialType}

mapper.select:
SELECT id, user_id, credential_type, secret_hash, salt, status, retry_count, next_retry_at, last_error, created_at, updated_at
FROM auth_credentials_pending
WHERE status = #{status}
  AND (next_retry_at IS NULL OR next_retry_at <= #{now})
ORDER BY updated_at ASC
LIMIT #{limit}
