mapper.select:
SELECT id, external_id, email, status, created_at, updated_at
FROM users
WHERE id = #{id}

SELECT id, external_id, email, status, created_at, updated_at
FROM users
WHERE email = #{email}

mapper.update:
UPDATE users
external_id = #{externalId}, email = #{email}, status = #{status}, created_at = #{createdAt}, updated_at = #{updatedAt},
WHERE id = #{id}
