mapper.select:
SELECT id, external_id, email, status, created_at, updated_at
FROM users

    AND id = #{id}
    AND external_id = #{externalId}
    AND email = #{email}
    AND status = #{status}
    AND created_at = #{createdAt}
    AND updated_at = #{updatedAt}

ORDER BY id DESC

SELECT id, external_id, email, status, created_at, updated_at
FROM users

    AND id = #{id}
    AND external_id = #{externalId}
    AND email = #{email}
    AND status = #{status}
    AND created_at = #{createdAt}
    AND updated_at = #{updatedAt}

ORDER BY id DESC
FOR UPDATE

mapper.update:
UPDATE users
external_id = #{externalId}, email = #{email}, status = #{status}, created_at = #{createdAt}, updated_at = #{updatedAt},
WHERE id = #{id}

update.foreach:
UPDATE users
external_id = #{item.externalId}, email = #{item.email}, status = #{item.status}, created_at = #{item.createdAt}, updated_at = #{item.updatedAt},
WHERE id = #{item.id}
